import { VmConfigurationLimitsJson } from './__json__/vm-configuration-limits-json';

/* istanbul ignore next: autogenerated */
export class VmConfigurationLimits {

  constructor(private _json: VmConfigurationLimitsJson) {
  }

  /**
   * Get max memory mebibytes.
   * @returns {number}
   */
  get maxMemoryMebibytes(): number {
    return this._json.max_memory_mebibytes;
  }

  /**
   * Get min memory mebibytes.
   * @returns {number}
   */
  get minMemoryMebibytes(): number {
    return this._json.min_memory_mebibytes;
  }

  /**
   * Get max cpu core count.
   * @returns {number}
   */
  get maxCpuCoreCount(): number {
    return this._json.max_cpu_core_count;
  }

  /**
   * Get max disk size mebibytes.
   * @returns {number}
   */
  get maxDiskSizeMebibytes(): number {
    return this._json.max_disk_size_mebibytes;
  }

  /**
   * Get min disk size mebibytes.
   * @returns {number}
   */
  get minDiskSizeMebibytes(): number {
    return this._json.min_disk_size_mebibytes;
  }

  /**
   * Get the json representation of this class.
   * @returns {VmConfigurationLimitsJson}
   */
  get json(): VmConfigurationLimitsJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
